DECLARE @tableName nvarchar(255) = 'TheTable'\nDECLARE @columnName nvarchar(255) = 'TheColumn'\n\nDECLARE @constraintName nvarchar(255)\n\nSELECT @constraintName = OBJECT_NAME([default_object_id]) \nFROM sys.columns \nWHERE [object_id] = OBJECT_ID(@tableName) AND [name] = @columnName\n\nIF @constraintName IS NOT NULL\nBEGIN\n    DECLARE @sql nvarchar(max) = 'ALTER TABLE [' + @tableName + '] DROP CONSTRAINT [' + @constraintName + ']'\n    EXEC sp_executesql @sql\nEND