DECLARE @MyXml xml = '<root/>'\nDECLARE @MyTable table (id int, value varchar(50))\n\nINSERT INTO @MyTable (id, value) VALUES (1, 'Value 1'), (2, 'Value 2'), (3, 'Value 3')\n\nSET @MyXml.modify('\n    insert {sql:column("t.XmlFragment")}\n    into (/root[1])\n')\nFROM (\n    SELECT (\n        SELECT id, value\n        FROM @MyTable\n        FOR XML PATH('myNode'), TYPE\n    ) AS XmlFragment\n) t\n\nSELECT @MyXml