CREATE TRIGGER trg_UpdateSmallField\nON YourTable\nAFTER UPDATE\nAS\nBEGIN\n    IF UPDATE(OtherField)  -- Check if OtherField was updated\n    BEGIN\n        UPDATE t\n        SET SmallField = 'NewValue'  -- Update SmallField\n        FROM YourTable t\n        INNER JOIN inserted i ON t.PrimaryKey = i.PrimaryKey  -- Join with inserted table\n        WHERE t.OtherField = i.OtherField  -- Filter on updated rows\n    END\nEND