CREATE TABLE Teams (\n  id INT PRIMARY KEY,\n  name VARCHAR(50) NOT NULL,\n  seed INT NOT NULL,\n  region VARCHAR(10) NOT NULL\n);\n\nCREATE TABLE Games (\n  id INT PRIMARY KEY,\n  round INT NOT NULL,  -- 1 = Round of 64, 2 = Round of 32, etc.\n  game_number INT NOT NULL,  -- unique within each round\n  team1_id INT NOT NULL,\n  team2_id INT NOT NULL,\n  FOREIGN KEY (team1_id) REFERENCES Teams(id),\n  FOREIGN KEY (team2_id) REFERENCES Teams(id)\n);\n\nCREATE TABLE Game_Outcomes (\n  id INT PRIMARY KEY,\n  game_id INT NOT NULL,\n  winner_id INT NOT NULL,\n  loser_id INT NOT NULL,\n  score VARCHAR(10) NOT NULL,  -- e.g. "75-65"\n  FOREIGN KEY (game_id) REFERENCES Games(id),\n  FOREIGN KEY (winner_id) REFERENCES Teams(id),\n  FOREIGN KEY (loser_id) REFERENCES Teams(id)\n);