SELECT P.FirstName, P.LastName, Count(A.attendance_date) AS CountOfattendance_date,\n       First(A.attendance_date) AS FirstOfattendance_date,\n       (SELECT COUNT(DISTINCT B.attendance_date)\n        FROM tblEventAttendance AS B\n        WHERE B.event_id = 8\n          AND B.attendance_date >= A.attendance_date) AS total\nFROM tblPeople AS P\nINNER JOIN tblEventAttendance AS A ON P.ID = A.people_id\nWHERE A.event_id = 8\nGROUP BY P.FirstName, P.LastName, A.attendance_date\n;