SELECT \n    p.name AS parameter_name,\n    t.name AS data_type,\n    p.max_length,\n    p.precision,\n    p.scale\nFROM \n    sys.sql_modules m\nINNER JOIN \n    sys.parameters p ON m.object_id = p.object_id\nINNER JOIN \n    sys.types t ON p.system_type_id = t.system_type_id\nWHERE \n    m.object_id = OBJECT_ID('your_stored_procedure_name');\n\nSELECT \n    c.name AS column_name\nFROM \n    sys.sql_modules m\nINNER JOIN \n    sys.columns c ON m.object_id = c.object_id\nWHERE \n    m.object_id = OBJECT_ID('your_stored_procedure_name');