CREATE TRIGGER trg_Delete_GrandChild\nON Parent\nINSTEAD OF DELETE\nAS\nBEGIN\n    DELETE FROM GrandChild\n    WHERE EXISTS (\n        SELECT 1\n        FROM deleted d\n        INNER JOIN Child1 c1 ON d.ParentID = c1.ParentID\n        INNER JOIN GrandChild gc ON c1.Child1ID = gc.Child1ID\n        WHERE gc.GrandChildID = GrandChild.GrandChildID\n    );\n\n    DELETE FROM GrandChild\n    WHERE EXISTS (\n        SELECT 1\n        FROM deleted d\n        INNER JOIN Child2 c2 ON d.ParentID = c2.ParentID\n        INNER JOIN GrandChild gc ON c2.Child2ID = gc.Child2ID\n        WHERE gc.GrandChildID = GrandChild.GrandChildID\n    );\n\n    DELETE FROM Child1\n    WHERE ParentID IN (SELECT ParentID FROM deleted);\n\n    DELETE FROM Child2\n    WHERE ParentID IN (SELECT ParentID FROM deleted);\n\n    DELETE FROM Parent\n    WHERE ParentID IN (SELECT ParentID FROM deleted);\nEND;