-- Create an insert trigger\nCREATE TRIGGER trg_Insert ON Warehouse_Table\nAFTER INSERT\nAS\nBEGIN\n    INSERT INTO Report_Table (column1, column2)\n    SELECT column1, column2\n    FROM inserted;\nEND;\n\n-- Create an update trigger\nCREATE TRIGGER trg_Update ON Warehouse_Table\nAFTER UPDATE\nAS\nBEGIN\n    UPDATE rt\n    SET rt.column1 = i.column1, rt.column2 = i.column2\n    FROM Report_Table rt\n    INNER JOIN inserted i ON rt.id = i.id;\nEND;