-- Create a user-defined table type\nCREATE TYPE IntegerList AS TABLE (Id INT PRIMARY KEY);\n\n-- Create a stored procedure that uses the table type\nCREATE PROCEDURE MyProcedure\n    @List1 IntegerList READONLY,\n    @List2 IntegerList READONLY\nAS\nBEGIN\n    -- Use the table variables in your query\n    SELECT * FROM @List1\n    UNION ALL\n    SELECT * FROM @List2\nEND;\n\n-- Create a sample list of integers\nDECLARE @List1 IntegerList;\nINSERT INTO @List1 (Id) VALUES (1), (2), (3);\n\nDECLARE @List2 IntegerList;\nINSERT INTO @List2 (Id) VALUES (4), (5), (6);\n\n-- Call the stored procedure with the table variables\nEXEC MyProcedure @List1, @List2;