CREATE PROCEDURE sp_GetPagedResults\n    @PageSize INT,\n    @PageNumber INT\nAS\nBEGIN\n    DECLARE @Start INT = (@PageNumber - 1) * @PageSize;\n    DECLARE @End INT = @Start + @PageSize;\n\n    CREATE TABLE #TempTable (\n        RowNum INT IDENTITY(1,1) PRIMARY KEY,\n        id INT,\n        name VARCHAR(255)\n    );\n\n    INSERT INTO #TempTable (id, name)\n    SELECT id, name\n    FROM YourTable\n    ORDER BY id;\n\n    SELECT *\n    FROM #TempTable\n    WHERE RowNum BETWEEN @Start + 1 AND @End;\n\n    DROP TABLE #TempTable;\nEND\nGO