DECLARE @tableName sysname = 'DelMe';\n\nWITH fkRelations AS (\n  SELECT \n    OBJECT_NAME(fk.parent_object_id) AS parentTable,\n    c.name AS columnName\n  FROM \n    sys.foreign_keys fk\n  INNER JOIN \n    sys.foreign_key_columns fkc ON fk.object_id = fkc.constraint_object_id\n  INNER JOIN \n    sys.columns c ON fkc.parent_object_id = c.object_id AND fkc.parent_column_id = c.column_id\n  WHERE \n    fk.referenced_object_id = OBJECT_ID(@tableName)\n)\nSELECT \n  'DELETE FROM ' + parentTable + ' WHERE ' + columnName + ' = ' + CONVERT(VARCHAR(10), id)\nFROM \n  fkRelations\nCROSS JOIN \n  DelMe\nORDER BY \n  parentTable;