SELECT \n    t.[name] AS [TableName],\n    ius.last_user_update AS [LastUpdate]\nFROM \n    sys.tables t \nINNER JOIN \n    sys.schemas s ON t.[schema_id] = s.[schema_id] \nINNER JOIN \n    sys.dm_db_index_usage_stats ius ON t.[object_id] = ius.[object_id] \nWHERE \n    t.[name] = 'YourTableName' AND \n    ius.database_id = DB_ID() AND \n    ius.user_seeks + ius.user_scans + ius.user_lookups + ius.user_updates <> 0 \nORDER BY \n    [LastUpdate] DESC;