-- Start a transaction to ensure atomicity\nSTART TRANSACTION;\n\n-- Temporarily disable foreign key constraint checks\nSET FOREIGN_KEY_CHECKS = 0;\n\n-- Delete records from the child tables\nDELETE FROM ChildTable1 WHERE parent_id IN (SELECT id FROM TBL WHERE condition);\n-- Repeat the DELETE statement for each child table\n\n-- Delete records from the parent table\nDELETE FROM TBL WHERE condition;\n\n-- Re-enable foreign key constraint checks\nSET FOREIGN_KEY_CHECKS = 1;\n\n-- Commit the transaction\nCOMMIT;