SELECT Name, \n       STUFF((SELECT ', ' + OptionName\n              FROM (\n                SELECT Name, OPTION1, OPTION2, OPTION3, OPTION4, OPTION5\n                FROM YourTable\n              ) AS SourceTable\n              UNPIVOT (\n                Options FOR OptionName IN (OPTION1, OPTION2, OPTION3, OPTION4, OPTION5)\n              ) AS UnpivotTable\n              WHERE Options = 1 AND Name = SourceTable.Name\n              FOR XML PATH('')), 1, 2, '') AS Options\nFROM YourTable