-- Assume 'source_database' is the original database\n-- and 'target_database' is the copied database\n\n-- Create a temporary table to store the source timestamp values\nCREATE TABLE #timestamp_values (\n    table_name sysname,\n    row_id int,\n    timestamp_value timestamp\n);\n\n-- Populate the temporary table with the source timestamp values\nINSERT INTO #timestamp_values\nSELECT \n    s.name, \n    r.row_id, \n    t.timestamp\nFROM \n    source_database.sys.tables s\nINNER JOIN \n    source_database.sys.schemas sc ON s.schema_id = sc.schema_id\nINNER JOIN \n    source_database.sys.columns c ON s.object_id = c.object_id AND c.name = 'timestamp'\nINNER JOIN \n    source_database.dbo.sysrows r ON s.object_id = r.id\nINNER JOIN \n    source_database.dbo.syscolumns t ON r.id = t.id AND t.colid = c.column_id;\n\n-- Update the target database with the preserved timestamp values\nUPDATE t\nSET t.timestamp = tv.timestamp_value\nFROM \n    target_database.sys.tables s\nINNER JOIN \n    target_database.sys.schemas sc ON s.schema_id = sc.schema_id\nINNER JOIN \n    target_database.sys.columns c ON s.object_id = c.object_id AND c.name = 'timestamp'\nINNER JOIN \n    target_database.dbo.sysrows r ON s.object_id = r.id\nINNER JOIN \n    #timestamp_values tv ON s.name = tv.table_name AND r.row_id = tv.row_id\nINNER JOIN \n    target_database.dbo.syscolumns t ON r.id = t.id AND t.colid = c.column_id;\n\n-- Drop the temporary table\nDROP TABLE #timestamp_values;