CREATE OR REPLACE PROCEDURE updateColumn(\n  p_pkey      NUMBER,  -- Assuming PKEY is of NUMBER type\n  p_columnName VARCHAR2,\n  p_value     VARCHAR2  -- Assuming the column to update is of VARCHAR2 type\n)\nAS\n  v_sql VARCHAR2(200);\nBEGIN\n  v_sql := 'UPDATE yourTableName SET ' || p_columnName || ' = :1 WHERE PKEY = :2';\n  EXECUTE IMMEDIATE v_sql USING p_value, p_pkey;\nEND;\n/