SELECT p.productId, p.colorsId, p.imgId, i.imgSrc, pc.colorName\nFROM products p\nLEFT JOIN (\n    SELECT DISTINCT imgId, colorId\n    FROM images\n    CROSS JOIN productcolors\n) ipc ON p.imgId = ipc.imgId AND p.colorsId = ipc.colorId\nLEFT JOIN images i ON ipc.imgId = i.imgId\nLEFT JOIN productcolors pc ON ipc.colorId = pc.colorId\nWHERE p.productId = 1;  -- Change the productId as needed