CREATE TABLE HeapTable (id INT, name VARCHAR(50));\nINSERT INTO HeapTable (id, name) VALUES (1, 'John'), (2, 'Jane'), (3, 'Bob');\nGO\n\n-- Check the table's physical structure\nSELECT \n    OBJECT_NAME(p.object_id) AS TableName,\n    p.index_id,\n    p.partition_number,\n    au.type_desc AS AllocationUnitType\nFROM sys.partitions p\nINNER JOIN sys.allocation_units au ON p.partition_id = au.container_id\nWHERE p.object_id = OBJECT_ID('HeapTable');