CREATE PROCEDURE MyReportProc\nAS\nBEGIN\n    -- Execute proc 1 asynchronously\n    EXECUTE ASYNC MyProc1;\n\n    -- Execute proc 2 asynchronously\n    EXECUTE ASYNC MyProc2;\n\n    -- Wait for both procs to complete\n    WAITFOR DELAY '00:00:05'; -- wait for 5 seconds\n\n    -- Retrieve results from both procs\n    SELECT * FROM #MyProc1Results;\n    SELECT * FROM #MyProc2Results;\nEND