ALTER PROCEDURE [EmployeeSearch]\n    @PayrollNumber  varchar(50) = null,\n    @isInternal     bit = null,\n    @Firstname      varchar(50) = null,\n    @Surname        varchar(50) = null\nAS\nSET NOCOUNT ON;\n\nSelect EmployeeSearchView.*\nFrom   EmployeeSearchView\nWhere\n    EmployeeSearchView.isInternal = @isInternal\nAND \n    (@PayrollNumber IS NULL OR EmployeeSearchView.PayrollNumber LIKE '%' + @PayrollNumber + '%')\nAND \n    (@Firstname IS NULL OR @Firstname <> '' OR EmployeeSearchView.Firstname LIKE '%' + @Firstname + '%')\nAND \n    (@Surname IS NULL OR @Surname <> '' OR EmployeeSearchView.Surname LIKE '%' + @Surname + '%')