-- Get the list of FKs referencing the original PK\nSELECT table_name, constraint_name, column_name\nFROM information_schema.referential_constraints\nWHERE referenced_table_name = 'table_name' AND referenced_column_name = 'original_pk';\n\n-- Update FKs to reference the new PK (composite key or UUID)\nUPDATE related_table\nSET fk_column = (SELECT new_pk FROM table_name WHERE original_pk = related_table.fk_column);