SELECT 'photo' as type, user_id, photo_id, photo_path, photo_name, date_added\nFROM photos\nWHERE user_id = 5\n\nUNION ALL\n\nSELECT 'video' as type, user_id, video_id, video_url, NULL, date_added\nFROM videos\nWHERE user_id = 5\n\nUNION ALL\n\nSELECT 'comment' as type, user_id, comment_id, comment, NULL, date_added\nFROM comments\nWHERE user_id = 5\n\nORDER BY date_added DESC\nLIMIT 10;