DECLARE @sql NVARCHAR(MAX) = ''\n\nSELECT @sql += 'EXEC sp_helptext ''' + QUOTENAME(s.name) + '.' + QUOTENAME(p.name) + ''''\nFROM sys.procedures p\nJOIN sys.schemas s ON p.schema_id = s.schema_id\n\nUNION ALL\n\nSELECT @sql += 'EXEC sp_helptext ''' + QUOTENAME(s.name) + '.' + QUOTENAME(f.name) + ''''\nFROM sys.objects f\nJOIN sys.schemas s ON f.schema_id = s.schema_id\nWHERE f.type IN ('FN', 'TF', 'IF')\n\nEXEC sp_executesql @sql