DECLARE @ProfileID INT;\nDECLARE @DefinitionTypes TABLE (DefinitionType VARCHAR(5));\n\n-- Populate DefinitionTypes table with all possible types\nINSERT INTO @DefinitionTypes (DefinitionType)\nVALUES ('TypeA'), ('TypeB'), ('TypeC'), ('TypeD'), ('TypeE'), ('TypeF'), ('TypeG'), ('TypeH'), ('TypeI'), ('TypeJ'), ('TypeK'), ('TypeL'), ('TypeM'), ('TypeN'), ('TypeO'), ('TypeP'), ('TypeQ'), ('TypeR'), ('TypeS'), ('TypeT'), ('TypeU'), ('TypeV'), ('TypeW'), ('TypeX'), ('TypeY'), ('TypeZ');\n\n-- Insert missing DefinitionTypes for each ProfileID of type ProfileTypeA\nINSERT INTO Definition (DefinitionType)\nSELECT dt.DefinitionType\nFROM Profile p\nCROSS JOIN @DefinitionTypes dt\nWHERE p.ProfileType = 'ProfileTypeA'\nAND NOT EXISTS (\n    SELECT 1\n    FROM Definition d\n    INNER JOIN DefinitionInProfile dip ON d.DefinitionID = dip.DefinitionID\n    WHERE dip.ProfileID = p.ProfileID AND d.DefinitionType = dt.DefinitionType\n);\n\n-- Insert into DefinitionInProfile for newly inserted DefinitionIDs\nINSERT INTO DefinitionInProfile (ProfileID, DefinitionID)\nSELECT p.ProfileID, d.DefinitionID\nFROM Profile p\nINNER JOIN Definition d ON NOT EXISTS (\n    SELECT 1\n    FROM DefinitionInProfile dip\n    WHERE dip.ProfileID = p.ProfileID AND dip.DefinitionID = d.DefinitionID\n)\nWHERE p.ProfileType = 'ProfileTypeA';