CREATE PROCEDURE insert_huge_blob()\nBEGIN\n  DECLARE chunk_size INT DEFAULT 1048576;  // 1 MB chunk size\n  DECLARE offset INT DEFAULT 0;\n  DECLARE file_size INT;\n  DECLARE chunk BLOB;\n\n  SET file_size = FILE_SIZE('c:/tmp/hugefile');\n\n  WHILE offset < file_size DO\n    SET chunk = LOAD_FILE('c:/tmp/hugefile', offset, chunk_size);\n    INSERT INTO table (bindata) VALUES (chunk);\n    SET offset = offset + chunk_size;\n  END WHILE;\nEND;