DECLARE\n  v_value VARCHAR2(50) := '1/22/2008P09RR8';\nBEGIN\n  FOR c IN (SELECT table_name, column_name \n            FROM all_tab_columns \n            WHERE data_type LIKE '%VARCHAR%') LOOP\n    EXECUTE IMMEDIATE 'SELECT COUNT(*) FROM ' || c.table_name || \n                      ' WHERE ' || c.column_name || ' = :1' \n    INTO v_count USING v_value;\n    IF v_count > 0 THEN\n      DBMS_OUTPUT.PUT_LINE(c.table_name || '.' || c.column_name);\n    END IF;\n  END LOOP;\nEND;