SELECT \n  d1.field_val AS 'First Name',\n  d2.field_val AS 'Last Name',\n  d3.field_val AS 'dob',\n  d4.field_val AS 'email',\n  d5.field_val AS 'phone'\nFROM \n  data d1\n  INNER JOIN data d2 ON d1.sub_id = d2.sub_id AND d2.field_name = 'Last Name'\n  INNER JOIN data d3 ON d2.sub_id = d3.sub_id AND d3.field_name = 'DOB'\n  INNER JOIN data d4 ON d3.sub_id = d4.sub_id AND d4.field_name = 'EMail'\n  INNER JOIN data d5 ON d4.sub_id = d5.sub_id AND d5.field_name = 'Telephone'\nWHERE \n  d1.field_name = 'First Name'\nORDER BY \n  d2.field_val;