DECLARE @tableName sysname = 'your_table_name';\n\nSELECT p.name, p.sql_handle\nINTO #procs\nFROM sys.sql_modules m\nJOIN sys.procedures p ON m.object_id = p.object_id\nWHERE m.definition LIKE '%' + @tableName + '%';\n\nDECLARE @procName sysname;\nDECLARE @sqlHandle varbinary(64);\n\nDECLARE cur CURSOR FOR\nSELECT name, sql_handle\nFROM #procs;\n\nOPEN cur;\n\nFETCH NEXT FROM cur INTO @procName, @sqlHandle;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    EXEC sp_recompile @procName;\n    FETCH NEXT FROM cur INTO @procName, @sqlHandle;\nEND\n\nCLOSE cur;\nDEALLOCATE cur;\n\nDROP TABLE #procs;