SELECT p.ProjectName, t.ThingName\nFROM Project p\nJOIN (\n  SELECT ptl.ProjectID, ptl.ThingID, ptl.CreatedDate\n  FROM ProjectThingLink ptl\n  INNER JOIN (\n    SELECT ThingID, MAX(CreatedDate) as MaxCreatedDate\n    FROM ProjectThingLink\n    GROUP BY ThingID\n  ) as latest\n  ON ptl.ThingID = latest.ThingID\n  AND ptl.CreatedDate = latest.MaxCreatedDate\n) as latest_link\nON p.ProjectID = latest_link.ProjectID\nJOIN Thing t\nON latest_link.ThingID = t.ThingID