CREATE OR REPLACE FUNCTION prevent_default_category_deletion()\nRETURNS TRIGGER AS $$\nBEGIN\n    IF OLD.id = (SELECT id FROM your_table ORDER BY id LIMIT 1) THEN\n        RAISE EXCEPTION 'Cannot delete default category';\n    END IF;\n    RETURN OLD;\nEND;\n$$ LANGUAGE plpgsql;\n\nCREATE TRIGGER prevent_default_category_deletion_trigger\nBEFORE DELETE ON your_table\nFOR EACH ROW EXECUTE PROCEDURE prevent_default_category_deletion();