SELECT \n    c.name, \n    t.name AS data_type, \n    c.collation_name\nFROM \n    sys.columns c\nINNER JOIN \n    sys.types t ON c.system_type_id = t.system_type_id\nWHERE \n    c.object_id = OBJECT_ID('Holiday') AND c.name = 'currency_cd';\n\nSELECT \n    c.name, \n    t.name AS data_type, \n    c.collation_name\nFROM \n    sys.columns c\nINNER JOIN \n    sys.types t ON c.system_type_id = t.system_type_id\nWHERE \n    c.object_id = OBJECT_ID('Holiday_XL') AND c.name = 'currency_cd';