SELECT \n    p.name AS 'DBO_Account', \n    p.type_desc AS 'Account_Type', \n    pm.permission_name AS 'Permission', \n    pm.state_desc AS 'Permission_State'\nFROM \n    sys.database_principals p\nINNER JOIN \n    sys.database_permissions pm ON p.principal_id = pm.grantee_principal_id\nWHERE \n    p.type IN ('SQL_USER', 'WINDOWS_USER') AND \n    pm.permission_name IN ('CONTROL', 'ALTER', 'CREATE', 'DROP');