CREATE FUNCTION dbo.RemoveNonNumeric (@str VARCHAR(MAX))\nRETURNS VARCHAR(MAX)\nAS\nBEGIN\n    DECLARE @Result VARCHAR(MAX) = ''\n    DECLARE @i INT = 1\n    WHILE @i <= LEN(@str)\n    BEGIN\n        IF SUBSTRING(@str, @i, 1) LIKE '[0-9]'\n            SET @Result = @Result + SUBSTRING(@str, @i, 1)\n        SET @i = @i + 1\n    END\n    RETURN @Result\nEND\nGO\n\nDECLARE @Num VARCHAR(20)\nSET @Num = ' + (82) 468 6152 '\n\nSELECT dbo.RemoveNonNumeric(@Num)