CREATE TRIGGER trg_MyTrigger ON MyTable\nFOR INSERT\nAS\nBEGIN\n    DECLARE @InnerTran INT;\n    SAVE TRANSACTION MyInnerTran;\n\n    BEGIN TRY\n        -- Perform operations on binary data and write to second table\n        INSERT INTO SecondTable (...)\n        SELECT ... FROM inserted;\n    END TRY\n    BEGIN CATCH\n        -- Rollback the inner transaction\n        ROLLBACK TRANSACTION MyInnerTran;\n    END CATCH;\n\n    -- Commit the inner transaction if no errors occurred\n    COMMIT TRANSACTION MyInnerTran;\nEND;