-- Get average row size for a table\nSHOW TABLE STATUS LIKE 'your_table_name';\n\n-- Get estimated row count for a table\nSELECT COUNT(*) FROM your_table_name;\n\n-- Calculate estimated space required for a table\nSET @avg_row_size = (SELECT AVG_ROW_LENGTH FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_NAME = 'your_table_name');\nSET @row_count = (SELECT COUNT(*) FROM your_table_name);\nSET @estimated_space = @avg_row_size * @row_count;\nSELECT @estimated_space;