SELECT\n    COALESCE(SUM(`order_items`.`quantity`), 0) as `count`,\n    `menu_items`.`name`\nFROM \n    `menu_items`\n    LEFT JOIN `order_items` ON `menu_items`.`id` = `order_items`.`menu_item_id`\n    LEFT JOIN `orders` ON `order_items`.`order_id` = `orders`.`id`\nWHERE \n    (`orders`.`date` IS NULL OR (`orders`.`date` >= '2008-11-01' AND `orders`.`date` <= '2008-11-30'))\nGROUP BY \n    `menu_items`.`id`