CREATE TABLE root_posts_meta (\n  id INT PRIMARY KEY AUTO_INCREMENT,\n  creation_time TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n  views INT DEFAULT 0,\n  user_id INT,  -- foreign key referencing the users table (not shown)\n  INDEX (creation_time)\n);\n\nCREATE TABLE root_posts_data (\n  id INT PRIMARY KEY,\n  title VARCHAR(255),\n  body TEXT,\n  FULLTEXT (title, body)\n);\n\nCREATE TABLE sub_posts_meta (\n  id INT PRIMARY KEY AUTO_INCREMENT,\n  root_post_id INT,  -- foreign key referencing root_posts_meta\n  creation_time TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n  user_id INT,  -- foreign key referencing the users table (not shown)\n  INDEX (root_post_id, creation_time)\n);\n\nCREATE TABLE sub_posts_data (\n  id INT PRIMARY KEY,\n  body TEXT,\n  FULLTEXT (body)\n);