SELECT \n  mt.SalesManCode,\n  SUM(CASE WHEN mt.TargetMonthYear = 'Jan' THEN mt.TargetValue END) AS JanTar,\n  SUM(CASE WHEN ms.SaleDate = 'Jan' THEN ms.AchievedValue END) AS JanAch,\n  SUM(CASE WHEN mt.TargetMonthYear = 'Feb' THEN mt.TargetValue END) AS FebTar,\n  SUM(CASE WHEN ms.SaleDate = 'Feb' THEN ms.AchievedValue END) AS FebAch,\n  ...\n  SUM(CASE WHEN mt.TargetMonthYear = 'Dec' THEN mt.TargetValue END) AS DecTar,\n  SUM(CASE WHEN ms.SaleDate = 'Dec' THEN ms.AchievedValue END) AS DecAch\nFROM \n  MonthlyTarget mt\n  LEFT JOIN MonthlySales ms ON mt.SalesManCode = ms.SalesManCode\nGROUP BY \n  mt.SalesManCode