CREATE TABLE measurement_unit (\n    id SERIAL PRIMARY KEY,\n    symbol VARCHAR(50) NOT NULL,  -- e.g., 'Âµg/l', 'mg/l', etc.\n    dimension VARCHAR(50) NOT NULL,  -- e.g., 'M/V', 'L/T', etc.\n    conversion_factor NUMERIC NOT NULL,  -- conversion factor to base unit\n    base_unit_id INTEGER NOT NULL,  -- references the base unit for this dimension\n    FOREIGN KEY (base_unit_id) REFERENCES measurement_unit (id)\n);