SELECT \n  SalesManCode,\n  [JanTar] = SUM(CASE WHEN MONTH(SaleDate) = 1 THEN TargetValue END),\n  [JanAch] = SUM(CASE WHEN MONTH(SaleDate) = 1 THEN AchievedValue END),\n  [FebTar] = SUM(CASE WHEN MONTH(SaleDate) = 2 THEN TargetValue END),\n  [FebAch] = SUM(CASE WHEN MONTH(SaleDate) = 2 THEN AchievedValue END),\n  ...\n  [DecTar] = SUM(CASE WHEN MONTH(SaleDate) = 12 THEN TargetValue END),\n  [DecAch] = SUM(CASE WHEN MONTH(SaleDate) = 12 THEN AchievedValue END)\nFROM \n  MonthlyTarget mt\n  LEFT JOIN MonthlySales ms ON mt.SalesManCode = ms.SalesManCode\nGROUP BY \n  SalesManCode