ALTER PROCEDURE [dbo].[sp_Report_InventoryAging]\n    @TitleFlag int=0,\n    @CompName varchar(100) = 'ALL COMPANIES',\n    @CompBranchId varchar(50) = 'ALL',\n    @StateId varchar(50)='All States'\nAS\nBEGIN\n    SELECT ...\n    FROM ...\n    WHERE TitleFlag = @TitleFlag\n    AND (@CompName = 'ALL COMPANIES' OR upper(Company) = upper(@CompName))\n    AND (@CompBranchId = 'ALL' OR OfficeID = @CompBranchId)\n    AND (@StateId = 'All States' OR StateID = @StateId)\nEND