SET CONSTRAINTS ALL DEFERRED;\n\n-- Perform your deletes and inserts here\n\nDECLARE\n  v_error_count NUMBER;\n  v_error_text  VARCHAR2(4000);\nBEGIN\n  DBMS_CONSTRAINTS.VALIDATE_DEFERRED_CONSTRAINTS(\n    error_count => v_error_count,\n    error_text  => v_error_text\n  );\n  \n  IF v_error_count > 0 THEN\n    DBMS_OUTPUT.PUT_LINE('Deferred constraint errors:');\n    DBMS_OUTPUT.PUT_LINE(v_error_text);\n  END IF;\nEND;\n\nROLLBACK;