CREATE TABLE YourTable (\n    ID INT PRIMARY KEY,\n    OtherTable1ID INT NULL,\n    OtherTable2ID INT NULL,\n    OtherTable3ID INT NULL,\n    CONSTRAINT CHK_OnlyOneColumnSet CHECK (\n        (OtherTable1ID IS NOT NULL AND OtherTable2ID IS NULL AND OtherTable3ID IS NULL)\n        OR (OtherTable1ID IS NULL AND OtherTable2ID IS NOT NULL AND OtherTable3ID IS NULL)\n        OR (OtherTable1ID IS NULL AND OtherTable2ID IS NULL AND OtherTable3ID IS NOT NULL)\n    )\n);