-- Create a new trace\nEXEC sp_trace_create @traceid = 1, @options = 2;\n\n-- Add events to the trace\nEXEC sp_trace_setevent @traceid = 1, @eventid = 10, @columnid = 1; -- RPC:Completed\nEXEC sp_trace_setevent @traceid = 1, @eventid = 12, @columnid = 1; -- SQL:BatchCompleted\n\n-- Start the trace\nEXEC sp_trace_start @traceid = 1;\n\n-- Stop the trace after 10 minutes\nWAITFOR DELAY '00:10:00';\nEXEC sp_trace_stop @traceid = 1;