-- See if the "country" table exists -- saving the result to a variable\nSELECT\n    @table_exists := COUNT(*)\nFROM\n    information_schema.TABLES\nWHERE\n    TABLE_SCHEMA = DATABASE() AND TABLE_NAME = 'country';\n\n-- Create the table if it doesn't exist\nCREATE TABLE IF NOT EXISTS country (\n    id INT unsigned auto_increment primary key,\n    name VARCHAR(64)\n);\n\n-- Insert data into the table if @table_exists = 0 (i.e., table is new)\nSET @insert_stmt = IF(@table_exists = 0, 'INSERT INTO country (name) VALUES (''Afghanistan''),(''Aland Islands'')', 'SELECT 1');\nPREPARE stmt FROM @insert_stmt;\nEXECUTE stmt;\nDEALLOCATE PREPARE stmt;