-- Create a stored procedure to reset the sequence\nCREATE OR REPLACE PROCEDURE reset_sequence\nIS\nBEGIN\n    EXECUTE IMMEDIATE 'ALTER SEQUENCE your_sequence_name RESTART START WITH 0';\nEND;\n/\n\n-- Create a scheduled job to run the procedure every year\nBEGIN\n    DBMS_SCHEDULER.CREATE_JOB (\n        job_name        => 'RESET_SEQUENCE_JOB',\n        job_type        => 'PLSQL_BLOCK',\n        job_action      => 'BEGIN reset_sequence; END;',\n        start_date      => SYSTIMESTAMP,\n        repeat_interval => 'FREQ=YEARLY; BYDATE=0101;',\n        end_date        => NULL,\n        enabled         => TRUE,\n        comments        => 'Reset sequence every year');\nEND;\n/