DECLARE @sql NVARCHAR(MAX) = '';\nSELECT @sql += 'CREATE ' + CASE WHEN is_unique = 1 THEN 'UNIQUE ' ELSE '' END + 'INDEX ' + name + ' ON your_table_name (' + cols + '); '\nFROM (\n    SELECT name, is_unique, STUFF((SELECT ', ' + col_name FROM sys.index_columns AS ic WHERE ic.object_id = i.object_id AND ic.index_id = i.index_id ORDER BY ic.index_column_id FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 2, '') AS cols\n    FROM sys.indexes AS i\n    WHERE i.object_id = OBJECT_ID('your_table_name') AND i.name IS NOT NULL\n) AS idx;\nEXEC sp_executesql @sql;