CREATE TABLE Users (\n  id INT PRIMARY KEY,\n  name VARCHAR(255),\n  email VARCHAR(255) UNIQUE\n);\n\nCREATE TABLE Friendships (\n  id INT PRIMARY KEY,\n  user_id INT,\n  friend_id INT,\n  FOREIGN KEY (user_id) REFERENCES Users(id),\n  FOREIGN KEY (friend_id) REFERENCES Users(id)\n);\n\nCREATE TABLE Events (\n  id INT PRIMARY KEY,\n  user_id INT,\n  event_type VARCHAR(255),\n  event_data TEXT,\n  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n  FOREIGN KEY (user_id) REFERENCES Users(id)\n);