CREATE TABLE Photos (\n    Id INT PRIMARY KEY,\n    Photo VARBINARY(MAX),\n    PhotoHash VARCHAR(64)\n);\n\n-- When inserting an image, calculate its hash\nINSERT INTO Photos (Photo, PhotoHash)\nVALUES (@Image, CONVERT(VARCHAR(64), HASHBYTES('SHA2_512', @Image), 2));\n\n-- To compare images, compare their hashes\nSELECT * FROM Photos\nWHERE PhotoHash = CONVERT(VARCHAR(64), HASHBYTES('SHA2_512', @Image), 2);