CREATE TABLE users (\n  id INT PRIMARY KEY,\n  name VARCHAR(255)\n);\n\nCREATE TABLE groups (\n  id INT PRIMARY KEY,\n  name VARCHAR(255),\n  parent_id INT REFERENCES groups(id)\n);\n\nCREATE TABLE acl_rules (\n  id INT PRIMARY KEY,\n  user_id INT REFERENCES users(id),\n  resource_id INT REFERENCES resources(id),\n  permission VARCHAR(255)\n);\n\nCREATE TABLE resources (\n  id INT PRIMARY KEY,\n  name VARCHAR(255)\n);